CartSetIdDefaultGeneratedType.value:
cartSetIdDefault = (cookieOptions?: CookieOptions) => {
  return (cartId: string) => {
    const headers = new Headers();
    headers.append(
      'Set-Cookie',
      stringify('cart', cartId.split('/').pop() || '', {
        path: '/',
        ...cookieOptions,
      }),
    );
    return headers;
  };
}

